$(function() {
	
	//设置滚动banner
    $("#scroll_img").slidesjs({
        width: 768,
        height: 397,
        navigation: false,
        play: {
            active: false,
            auto: true,
            interval: 4000,
            swap: true
        }
    })
	
	var $appendTxt = '<div class="pop_cont"><div class="gift_info"><div>恭喜你，初窥门道，截获一个神秘的礼包暗号:</div><div class="anhao f_red">乐享无极</div><div style="padding:0 10px;">加入官方Q群：335862074，联系客服MM发出暗号，即可获得<span class="f_red">价值888元的游戏尊享礼包！</span></div><a onclick="giftClose();" class="btn_close">确定</a></div><div class="shade"></div></div>';
	
	/*var $appendTxt = '<div class="pop_cont"><div class="gift_info"><div>活动暂未开启，敬请期待，谢谢。</div><a onclick="giftClose();" class="btn_close">确定</a></div><div class="shade"></div></div>';*/
	
	//弹出礼包窗口
	$(".btn_gift").click(function(){
		var $winTop = $(window).scrollTop();
		
		$("#giftPop").append($appendTxt);
		//设置遮罩高度
		$(".shade").css("height",$(document).height());
		
		//设置弹窗高度
		$(".gift_info").css("top",$winTop + 130);
	});
	
	//判断浏览器
	var browser = {
		versions: function () {
			var u = navigator.userAgent, app = navigator.appVersion;
			return {//移动终端浏览器版本信息
				trident: u.indexOf('Trident') > -1, //IE内核
				presto: u.indexOf('Presto') > -1, //opera内核
				webKit: u.indexOf('AppleWebKit') > -1, //苹果、谷歌内核
				gecko: u.indexOf('Gecko') > -1 && u.indexOf('KHTML') == -1, //火狐内核
				mobile: !!u.match(/AppleWebKit.*Mobile.*/), //是否为移动终端
				ios: !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/), //ios终端
				android: u.indexOf('Android') > -1 || u.indexOf('Linux') > -1, //android终端或uc浏览器
				iPhone: u.indexOf('iPhone') > -1, //是否为iPhone或者QQHD浏览器
				iPad: u.indexOf('iPad') > -1, //是否iPad
				webApp: u.indexOf('Safari') == -1 //是否web应该程序，没有头部与底部
			};
		}(),
		language: (navigator.browserLanguage || navigator.language).toLowerCase()
	}
		
	$(".down-btn").click(function(){
		if (browser.versions.mobile) {//判断是否是移动设备打开。browser代码在下面
			var ua = navigator.userAgent.toLowerCase();//获取判断用的对象
			//在微信中打开
			if (ua.match(/MicroMessenger/i) == "micromessenger") {
				_czc.push(["_trackEvent", "微信应用宝包", "游戏下载", "微信"]);
				/*alert("微信端安装包暂未开放，敬请期待！");*/
				$(this).attr("href","http://a.app.qq.com/o/simple.jsp?pkgname=com.tencent.tmgp.zywjsmz"); 
				/*window.location.href="http://a.app.qq.com/o/simple.jsp?pkgname=com.tencent.tmgp.zywjsmz"; */
				return true;
			}
			
			//是否在安卓浏览器打开
			else if(browser.versions.android){
				_czc.push(["_trackEvent", "手机安卓包", "游戏下载", "手机官网"]);
				window.open("http://cdn.smz.wuji.com/01wj/moshen.wuji_A.1225.apk");
			}
			
			//是否在IOS浏览器打开
			else if (browser.versions.ios) {
				_czc.push(["_trackEvent", "手机iOS包", "游戏下载", "手机官网"]);
				alert("iOS版正在研发中，敬请期待，谢谢！");
			}
			
			else{
				_czc.push(["_trackEvent", "手机安卓包", "游戏下载", "手机官网"]);
				window.open("http://cdn.smz.wuji.com/01wj/moshen.wuji_A.1225.apk");
			}
			
		} 
	});
	
	$('.btn_android').click(function(){
		if (browser.versions.mobile) {//判断是否是移动设备打开。browser代码在下面
			var ua = navigator.userAgent.toLowerCase();//获取判断用的对象
			//在微信中打开
			if (ua.match(/MicroMessenger/i) == "micromessenger") {
				_czc.push(["_trackEvent", "微信应用宝包", "游戏下载", "微信"]);
				/*alert("微信端安装包暂未开放，敬请期待！");*/
				$(this).attr("href","http://a.app.qq.com/o/simple.jsp?pkgname=com.tencent.tmgp.zywjsmz"); 
				/*window.location.href="http://a.app.qq.com/o/simple.jsp?pkgname=com.tencent.tmgp.zywjsmz"; */
				return true;
			}
			
			//是否在安卓浏览器打开
			else if(browser.versions.android){
				_czc.push(["_trackEvent", "手机安卓包", "游戏下载", "手机官网"]);
			}
			
			else{
				_czc.push(["_trackEvent", "手机安卓包", "游戏下载", "手机官网"]);
				window.open("http://cdn.smz.wuji.com/01wj/moshen.wuji_A.1225.apk");
			}
		} 
	});
});

//关闭礼包窗口
function giftClose() {
		$(".pop_cont").remove();
	}

//分享
var share_title = "大型RPG仙侠风手游《无极神魔传》";
var share_link = "http://smz.wuji.com";
var share_pic = "../images/banner/img_01.jpg";
var share_summary = "开启征战大陆的噬血仙途，引领最绚丽的自由动作潮流！";
var share_site = "http://smz.wuji.com";


function shareTSina() {
    e = "http://service.weibo.com/share/share.php?pic=" + encodeURIComponent(share_pic) + "&title=" + encodeURIComponent(share_title.replace(/&nbsp;/g, " ").replace(/<br \/>/g, " ")) + "&url=" + encodeURIComponent(share_link);
    window.location.href = e;
}

function shareQzone() {
    e = 'http://sns.qzone.qq.com/cgi-bin/qzshare/cgi_qzshare_onekey?url=' + encodeURIComponent(share_link) + '&title=' + encodeURI(share_title) + '&summary=' + encodeURIComponent(share_summary);
    window.location.href = e;
}

function shareToWb() {
    e = 'http://v.t.qq.com/share/share.php?url=' + encodeURIComponent(share_link) + '&title=' + encodeURI(share_title) + '&appkey=' + encodeURI(share_site);
    window.location.href = e;
}